  const [selected, setSelected] = useState(0)
  const randomQuote = () => {
    const randomIndex = Math.floor(Math.random() * anecdotes.length)
    setSelected(randomIndex)
  }
  const [votes, setVotes] = useState(votes)
  const voteQuote = () => {
    const currentQuoteVote = votes[selected] += 1
    setVotes(currentQuoteVote)
    console.log(currentQuoteVote)
    return currentQuoteVote;
    // return ++votes[selected];
  }

  return (
    <div>
      <h1>Anecdotes</h1>
      <p>{anecdotes[selected]}</p>
      <p>Votes: {votes[selected]}</p>
      {console.log(votes[selected])}
      <button onClick={voteQuote}>Vote</button>
      <button onClick={randomQuote}>Random Quote</button>
    </div>
  )
}

export default App
