import { useState } from 'react'

const App = () => {
  const [ counter, setCounter ] = useState(0)
  const [ newCounter, setNewCounter ] = useState(0)


  // const handleClick = () => {
  //   console.log('clicked')
  // }

  return (
    <>
    <div>
      <div>{counter}</div>

      <button onClick={() => setCounter(counter + 1)}>
        Plus
      </button>
      <button onClick={() => setCounter(0)}> 
        Zero
      </button>
    </div>
    <div>
      <div>{newCounter}</div>

      <button onClick={() => setNewCounter(newCounter + 1)}>
        Plus
      </button>
      <button onClick={() => setNewCounter(0)}> 
        Zero
      </button>
    </div>
    </>
  )
}

export default App
