const Header = (props) => {
  <>
    <h1>{props.course}</h1>
  </>
}

const Part = (props) => {
  <p>
    {props.part.name} {props.part.exercises}
  </p>
}

const Content = (props) => {
  return (
    <>
      <p>
        <Part part={props.parts[0]} exercises={props.parts[0]} />
        <Part part={props.parts[1]} exercises={props.parts[1]} />
        <Part part={props.parts[2]} exercises={props.parts[2]} />
      </p>
    </>
  )
}

const Total = (props) => <p>Number of exercises {props.value}</p>

const App = () => {

  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10
      },
      {
        name: 'Using props to pass data',
        exercises: 7
      },
      {
        name: 'State of a component',
        exercises: 14
      }
    ]
  }

  return (
    <div>
      <Header course={course.name} />
      <Content parts={course.parts} />
      <Total value={[course.parts[0].exercises] + [course.parts[1].exercises] + [course.parts[2].exercises]} />
    </div>
  )
}

export default App
